import "../contracts/interfaces/IWhitelist.sol";

contract Verifier is IWhitelist{
    using ECDSA for bytes32;
    uint256 constant chainId = 0x15b3;
    address constant verifyingContract = 0x1C56346CD2A2Bf3202F771f50d3D14a367B48070;
    bytes32 constant salt = 0xf2d857f4a3edcb9b78b4d503bfe733db1e3f6cdc2b7971ee739626c97e86a558;

    address public verifyAddress;

    string private constant EIP712_DOMAIN  = "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract,bytes32 salt)";
    string private constant BID_TYPE = "Bid(uint256 amount,address account)";

    bytes32 private constant EIP712_DOMAIN_TYPEHASH = keccak256(abi.encodePacked(EIP712_DOMAIN));

    bytes32 private constant BID_TYPEHASH = keccak256(abi.encodePacked(BID_TYPE));
    bytes32 private constant DOMAIN_SEPARATOR = keccak256(abi.encode(
        EIP712_DOMAIN_TYPEHASH,
        keccak256("My amazing dApp"),
        keccak256("2"),
        chainId,
        verifyingContract,
        salt
    ));

    struct Bid {
        uint256 amount;
        address account;
    }

    function hashBid(Bid memory bid) private pure returns (bytes32){
        return keccak256(abi.encodePacked(
            "\x19\x01",
            DOMAIN_SEPARATOR,
            keccak256(abi.encode(
                BID_TYPEHASH,
                bid.amount,
                bid.account
            ))
        ));
    }

    function setVerifyAddress(address addr) public{
        verifyAddress = addr;
    }

    function verify(uint256 amount,address account,bytes calldata signature) override public view returns(bool) {
        Bid memory bid = Bid({
            amount: amount,
            account: account
        });

        address recoveredAddress = hashBid(bid).recover(signature);
        return recoveredAddress==verifyAddress;
    }
}

